mapper.select:

		select * 
			from (select @rownum := @rownum + 1 rn,
					 	c.* 
			  			from ( select * from( select a.bno, a.user_id, a.boardtype, a.title, a.text, a.price, a.img, a.m_registdate, a.m_updatedate, b.c_code from (
                            				select b.bno, b.user_id, b.c_code, b.boardtype, b.title, b.text, b.price, b.m_registdate, b.m_updatedate, a.img 
                            				from IMGBOARD a right outer join MAINBOARD b on a.bno = b.bno group by b.bno, b.user_id, b.c_code, b.boardtype, b.title, b.text, b.price, b.m_registdate, b.m_updatedate, a.bno, a.img
                            				) a 
                            inner join CATEGORYBOARD b on a.c_code = b.c_code
                            where

					1=1
					a.c_code = #{c_code}
					 bigCategory = #{typeValue}
					 middleCategory = #{typeValue}

					  				) Y
			  		where


					1 = 1
					text like concat('%', #{searchName} ,'%') or title like concat('%', #{searchName} ,'%')
                    title like concat('%', #{searchName} ,'%')
                    text like concat('%', #{searchName} ,'%')
                    user_ID like concat('%', #{searchName} ,'%')


			  		order by bno desc) c ) Z, (select @rownum := 0) q
		where rn > (#{pageNum} - 1) * #{amount} and rn <= #{pageNum} * #{amount}


		select count(*) as total from (select a.bno, a.user_ID, a.boardType, a.title, a.text, a.price, a.M_registDate, a.M_updateDate, b.c_code, b.bigCategory, b.middleCategory, b.smallCategory from MAINBOARD a inner join CATEGORYBOARD b on a.c_code = b.c_code where

					1=1
					a.c_code = #{c_code}
					bigCategory = #{typeValue}
					middleCategory = #{typeValue}

			  		) X
			  where


			1 = 1
			text like concat('%', #{searchName} ,'%') or title like concat('%', #{searchName} ,'%')
            title like concat('%', #{searchName} ,'%')
            text like concat('%', #{searchName} ,'%')
            user_ID like concat('%', #{searchName} ,'%')
